/**
 * @file wifi_task.cpp
 * @brief Implements the background task for WiFi and automatic time sync.
 *
 * Fetches timezone via HTTPS, saves it to NVS, syncs time via NTP, and
 * updates the hardware RTC with the correct UTC time. Now forces a system-wide
 * timezone update.
 */

#include "wifi_task.h"
#include "../config.h"
#include "../certs.h"
#include <WiFi.h>
#include <WiFiClientSecure.h>
#include <WiFiProvisioner.h>
#include <HTTPClient.h>
#include <ArduinoJson.h>
#include <RTClib.h>
#include "clock_task.h"
#include "time.h"
#include <Preferences.h>
#include <stdlib.h> // Required for setenv
#include <TzDbLookup.h>
#include "Adafruit_ThinkInk.h"

// --- Externs for shared resources ---
extern RTC_DS3231 rtc;
extern Adafruit_IL0373 display;
extern QueueHandle_t clockCommandQueue;
extern TaskHandle_t clockTaskHandle;
extern char time_zone[64];
extern Preferences preferences;
const String useragent = "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36";
extern QueueHandle_t networkEventQueue;

extern TaskHandle_t epdTask;
extern QueueHandle_t epdqueue;

void WiFiEvent(WiFiEvent_t event)
{
    BaseType_t xHigherPriorityTaskWoken = pdFALSE;
    NetworkEvent_t evt;

    switch (event)
    {
    case ARDUINO_EVENT_WIFI_STA_GOT_IP:
        //Serial.println("WiFi Event: GOT IP");
        evt = WIFI_EVENT_CONNECTED;
        // Use the ISR-safe version to send to the queue
        xQueueSendFromISR(networkEventQueue, &evt, &xHigherPriorityTaskWoken);
        break;
    case ARDUINO_EVENT_WIFI_STA_DISCONNECTED:
        //Serial.println("WiFi Event: DISCONNECTED");
        evt = WIFI_EVENT_DISCONNECTED;
        xQueueSendFromISR(networkEventQueue, &evt, &xHigherPriorityTaskWoken);
        break;
    default:
        break;
    }
}

